import React, { Component } from 'react';

class ShowList extends React.Component {

	constructor(){
		super();
		this.state = {
		  input: ""
		};
	  }

	  handleChange = (event) => {
		this.setState({input: event.target.value})
	  };
	  handleSubmit = () => {
		this.props.addToDo(this.state.input);
		this.setState({input: ""})
	  };
	  render() {
		return (
		  <div className="form-group">
			<input type="text" className="form-control" value={this.state.input} onChange={this.handleChange}/><br/><br/> 
			<button type="button" className="btn btn-primary" onClick={this.handleSubmit}>Add</button><br/><br/> 
		  </div>
		);
	  }
	}


export default ShowList;
