import React from 'react';
import logo from './logo.svg';
import './App.css';
import ShowList from './ShowList';
import Axios from 'axios';


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      todos: []
    };
  }

  componentDidMount() {
    Axios.get("https://www.facebook.com/groups/1389776634498901").then
    (response => {
      this.setState({
        todos: response.data
      });
    });
  }

  addToDo = input => {
    console.log(input)
    Axios.post("http://8fc9b0d5.ngrok.io/todos", {
      todo: {
        title: input
      }
    })
  }

  render(){
    return (
      <div className="container">
        <nav className="navbar navbar-light bg-light">
        <span className="navbar-brand mb-0 h2">Feeds</span>
        </nav><br/><br/>
        <ShowList items={this.state.todos}/>
        <ShowList/>
      </div>  
    );
  }
}

export default App;
